import React, { useState } from 'react';
import axios from 'axios';

function ImageUploader() {
    const [file, setFile] = useState(null);
    const [caption, setCaption] = useState('');
    const [loading, setLoading] = useState(false);

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
        setCaption(''); // Clear caption when a new file is selected
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (!file) return;

        setLoading(true);
        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await axios.post('http://localhost:5000/generate-caption', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            setCaption(response.data.caption);
        } catch (error) {
            console.error("Error uploading file:", error);
            setCaption('Error generating caption');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ textAlign: 'center', padding: '20px' }}>
            <h2>Image Caption Generator</h2>
            <form onSubmit={handleSubmit}>
                <input type="file" onChange={handleFileChange} accept="image/*" />
                <button type="submit" disabled={loading}>
                    {loading ? 'Generating...' : 'Upload and Generate Caption'}
                </button>
            </form>
            {caption && (
                <div style={{ marginTop: '20px' }}>
                    <h3>Generated Caption:</h3>
                    <p>{caption}</p>
                </div>
            )}
        </div>
    );
}

export default ImageUploader;
